<script>

jQuery(document).on('click', '.getpost1', function() {
	var pageno = jQuery(this).attr('rel');
	jQuery('.getpost1').removeClass("active");
	
	jQuery.ajax({
		url : '<?php echo admin_url( 'admin-ajax.php' ); ?>' , 
		type: 'POST',
		data: {action:"get_all_post_per_page",paged:pageno},
		beforeSend: function() {
			jQuery('.popup').fadeIn(350);
		},
		success	: function (result)
		{
			jQuery('.popup').fadeOut(350);
			jQuery("#allposts").html(result);
			jQuery("#page_"+pageno).addClass("active");
			return false;
		}
	});
});

jQuery(document).on('click', '.getpostbycat', function() {
	var pageno = jQuery(this).attr('rel');
	var slug = jQuery(this).data('cat');
	jQuery('.getpostbycat').removeClass("active");
	
	jQuery.ajax({
		url : '<?php echo admin_url( 'admin-ajax.php' ); ?>' , 
		type: 'POST',
		data: {action:"get_post_per_page_by_cat",paged:pageno,slug:slug},
		beforeSend: function() {
			jQuery('.popup').fadeIn(350);
		},
		success	: function (result)
		{
			jQuery('.popup').fadeOut(350);
			jQuery("#allcatposts").html(result);
			jQuery("#catpage_"+pageno).addClass("active");
			return false;
		}
	});
});

